import { Provider } from 'react-redux'
import App from "next/app";
import React from "react";
import { createStore } from 'redux'
import Store from '../store/configStore'
import withRedux from "next-redux-wrapper";
//import {makeStore} from "../lib/redux";
import doLogin from '../store/reducers/LoginReducer'
const makeStore = (initialState, options) => {
    return createStore(doLogin, initialState);
 };
 

class NextJsKiosco extends App {

    static async getInitialProps({Component, ctx}) {
        console.log("Solo entre al app una vez")
          // we can dispatch from here too
          ctx.store.dispatch({type: 'DO_LOGIN', value: false});
      
          const pageProps = Component.getInitialProps ? await Component.getInitialProps(ctx) : {};
        
          return {pageProps};
        }
  
      render() {
          const {Component, pageProps, store} = this.props;
  
          //console.log(this.props.data); <PersistGate persistor={store.__persistor} loading={<div>Loading</div>}>
          return (
              
              <Provider store={Store}>
                
                  <Component {...pageProps} />
                
              </Provider>
          );
      }
  
  }
  

    
    export default withRedux(makeStore)(NextJsKiosco);
