import React, { Component } from 'react';
import { connect } from 'react-redux'
import Router from 'next/router'
import PlantillaG from '../../componentes/PlantillaG';
import fetch from 'isomorphic-unfetch';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import '../../componentes/styles/nombre.scss'
const textinputdisabled = {
    background: '#e0e0e0',
    borderRadius: 3,
    fontSize: 18,
    fontFamily: 'Garamond',
    color: 'black',
    height: 35,
    
    padding: '0 20px',
   
   
  }
class maquinaprofile extends Component {
    constructor(props){
        super(props)
        let datamaquina = this.props.maquina
        this.state = {
         
            queryval : this.props.queryval,
            maquina : datamaquina
        }
    }
    render() {
        console.log(this.state.maquina[0].nombre)
        return (
           
            <PlantillaG>
   
      <Grid container spacing={3}  justify="center"
         alignItems="center">
      <Grid item xs={12}>
      <Paper className="paper">
        <table>
            <tbody>           <tr>
               <td>
                   Nombre Maquina : 
               </td>
               <td>
               <input type="text" style={textinputdisabled}  value={this.state.maquina[0].nombre} onChange={this.handleNameChange} />
                
               </td>
           </tr>
           <tr>
               <td>
                   Ip Maquina : 
               </td>
               <td>
               <input type="text" style={textinputdisabled} value={this.state.maquina[0].ip_maquina} onChange={this.handleNameChange} />
                
               </td>
           </tr>
           <tr>
               <td>
                   NÂ° Tarjetas disponibles : 
               </td>
               <td>
               <input type="text" style={textinputdisabled} value={this.state.maquina[0].n_tarjetas} onChange={this.handleNameChange} />
                
               </td>
           </tr>
           </tbody>

        </table>
      </Paper>
      </Grid>
      </Grid>
            </PlantillaG>
        
        
        );
    }

   
}
maquinaprofile.getInitialProps = async({ query,store}) => {
  
    //const res = await fetch('http://localhost:8080/users/'+ query.id);
    //const data = await res.json();
   // console.log('https://winged-pen-261210.appspot.com/users/'+ query.id)
      console.log(query.nombre);
      const res = await fetch('http://localhost:8081/maquinas/' + query.nombre);
      const data = await res.json();
      
      //console.log(`Show data fetched. Count: ${data.length}`  );
      
   
     
      return{
        queryval:query.id,
        maquina :data
      }
      
  }
  
export default connect(
    (state) => state) (maquinaprofile)
