import React, { Component } from 'react';
import PlantillaG from './PlantillaG';
import { Button } from '@material-ui/core';
const stylebutton = {
    background: "#e95a0c",
    borderRadius: 3,
    border: 0,
    color: 'white',
    width : '100%',
    height: 48,
    padding: '0 10px',
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
   // marginLeft: 20,
    marginTop: 5,
   // marginRight: 20
  };
  
class LoginFormulario extends Component {

    constructor(props){
       super(props) 
       this.state={
           isloged: this.props.isloged,
           usuario : "",
           pass : ""
       }
       
    }
    handleNameChange(event) { 
        this.setState({usuario: event.target.value});
      }
      handlepasswordChange(event) {  
        this.setState({pass: event.target.value});
      }
      _login(data) {
        return fetch('http://localhost:8081/usuarios/login', {
            method: 'POST',
             
            body: JSON.stringify(data),
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(res => {
           console.log("Reponse" + JSON.stringify(res))
            return res;
        }).catch(err => err);
    }
      handlelogin(){
        var _userdata = {user_passwd:this.state.usuario+"_"+this.state.pass}
        this._login(_userdata).then(res =>{
            console.log("usuario logeado correctamente")
         return res
        }
        ).catch(err => err);
    }
   
    render() {
        return (
            <PlantillaG>
            <h2>Inicio de sesion para el acceso a la BD </h2>
            <table>
           <tbody>
             <tr>
               <th>
                Usuario
               </th>
               <th>
           <input type="text"  onChange={this.handleNameChange} />
               
               </th>
             </tr>
         
            
        
             <tr>
               <th>
                Contrase√±a 
               </th>
               <th>
               <input type="password" onChange={this.handlepasswordChange} />
               </th>
             </tr>
             <tr >
               <th><Button variant="contained"  style={stylebutton} onSubmit={this.handlelogin()} >iniciar sesion</Button>
               </th>
             </tr>
           </tbody>
         </table>
          </PlantillaG>
   
          
        );
    }
}

export default LoginFormulario;
